//string -> string
export const encode = s => encodeURIComponent(s)
//string -> string
export const decode = s => decodeURIComponent(s)
//...string -> log ...string
export const log    = (...args) => console.log(...args)
//object -> string
export const stringifyJSON = json => JSON.stringify(json)
//string -> object
export const parseJSON = s => JSON.parse(s)
