import { assert } from 'chai'

import {
  compose,
  log, parseQuery, stringifyQuery,  //url
  moment, addDay, addHour
} from '../src'

describe('url test', () => {

  describe('将url search 转换成 object', () => {
    it('should return a obj', () => {
      const search = '?a=1%3d&b=2'
      const r = parseQuery(search)
      // log('result', r)
      assert.equal('object', typeof r)
    })
  })

  describe('将object 转换成 url search', () => {
    it('should return a string', () => {
      const json   = { a: '1=', b: 2 }
      const r = stringifyQuery(json)
      // log('result', r)
      assert.equal('string', typeof r)
    })
  })

})
